jQuery.noConflict();jQuery("document").ready(function(){jQuery(function(){jQuery("a[rel=popover]").popover({delay:{hide:500,show:80},placement:function(b,a){var c=jQuery(a).offset();height=jQuery(document).outerHeight();width=jQuery(document).outerWidth();vert=0.5*height-c.top;vertPlacement=vert>0?"bottom":"top";horiz=0.5*width-c.left;horizPlacement=horiz>0?"right":"left";placement=Math.abs(horiz)>Math.abs(vert)?horizPlacement:vertPlacement;return placement}})})});